/*!
 * Messenger App Icon
 * Custom Implementation based on ScrollUp Plugin
 * Author: Adapted for Messenger Integration
 */

(function ($, window, document) {
    "use strict";
  
    // Initialize Messenger App Icon
    $.fn.messengerAppIcon = function (options) {
      // Default settings
      const defaults = {
        messengerName: "messengerIcon",
        messengerLink: "https://m.me/YOUR_PAGE_ID", // Replace with your Messenger link
        iconText: "Chat with us", // Optional text
        iconImg: false, // Set to true if using a custom image
        iconImgSrc: "", // Custom image source
        position: "bottom-right", // Icon position: bottom-right, bottom-left
        zIndex: 2147483647,
      };
  
      // Merge user options with defaults
      const settings = $.extend({}, defaults, options);
  
      // Create the icon element
      const $icon = $("<a/>", {
        id: settings.messengerName,
        href: settings.messengerLink,
        target: "_blank",
        title: settings.iconText,
        html: settings.iconImg
          ? `<img src="${settings.iconImgSrc}" alt="Messenger Icon">`
          : settings.iconText,
      });
  
      // Add styles to the icon
      $icon.css({
        display: "block",
        position: "fixed",
        zIndex: settings.zIndex,
        padding: "10px 15px",
        backgroundColor: "#0084ff",
        color: "#fff",
        borderRadius: "50px",
        textAlign: "center",
        boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
        cursor: "pointer",
        fontSize: "14px",
        textDecoration: "none",
      });
  
      // Position the icon
      if (settings.position === "bottom-right") {
        $icon.css({ bottom: "20px", right: "20px" });
      } else if (settings.position === "bottom-left") {
        $icon.css({ bottom: "20px", left: "20px" });
      }
  
      // Append to the body
      $("body").append($icon);
  
      // Optional hover effect
      $icon.hover(
        function () {
          $(this).css({ backgroundColor: "#0056b3" });
        },
        function () {
          $(this).css({ backgroundColor: "#0084ff" });
        }
      );
    };
  
    // Automatically initialize the Messenger icon
    $(document).ready(function () {
      $(document).messengerAppIcon({
        messengerLink: "https://m.me/460078043846871", // Replace with your page ID
        iconText: "Chat with us!", // Customize text
        position: "bottom-right", // Can be "bottom-left"
        iconImg: false, // Set true and provide iconImgSrc for custom image
      });
    });
  })(jQuery, window, document);
  